# TAILWIND INSTALL
npm install -D tailwindcss

# AUTOPREFIXER INSTALL
npm install -D autoprefixer

# TAILWIND CSS BASE
printf '@tailwind base;\n@tailwind components;\n@tailwind utilities;\n' > dist/input.css

# TAILWIND CONFIG
printf '/** @type {import('tailwindcss').Config} */\nmodule.exports = {\n  content: ["./dist/*.css", "./public/asset/**/*.{css,js,html}", "./template/*.{php,js,css,html}", "./template/**/*.{php,js,css,html}"],\n  theme: {\n    extend: {},\n  },\n  plugins: [],\n}' > tailwind.config.js

# TEMPLATE HEADER
printf '<!doctype html>\n<html>\n<head>\n<meta charset="UTF-8">\n<meta name="viewport" content="width=device-width, initial-scale=1.0">\n<title>'"$project_name"'</title>\n<link href="/asset/css/style.css" rel="stylesheet">\n</head>\n<body>\n' > template/header.html

# TEMPLATE FOOTER
printf '</body>\n</html>\n' > template/footer.html

# INDEX FILE
printf '<?php \ninclude("../template/header.html"); \n?>\n\n<?php\n$page = filter_input(INPUT_GET, "page", FILTER_DEFAULT);\n$module = $page ?? "home";\nif(!include("../template/module/{$module}.html")){\n\techo("<h1>Page not found</h1>");};\n?>\n\n<?php\ninclude("../template/footer.html");\n?>\n' > public/index.php

# HTACCESS
echo -e '<IfModule mod_rewrite.c>\nOptions +FollowSymlinks\nRewriteEngine on\nRewriteBase /\n\nRewriteCond %{REQUEST_FILENAME} !-d\nRewriteCond %{REQUEST_FILENAME} !-f\nRewriteRule ^page/([^/]*)$ /index.php?page=$1 [L]\n\n</IfModule>' > public/.htaccess
echo -e 'RewriteEngine On\nRewriteRule ^$ /public/index.php [L]' > .htaccess

# GITIGNORE
echo -e 'project-ini.sh\nproject-server.sh\nproject-watch.sh' > .gitignore

# CREATE FILE SERVER
echo -e '#!/bin/sh\nphp -S localhost:8123 -t public' > project-server.sh

# CREATE FILE WATCH CSS
echo -e '#!/bin/sh\nnpx tailwindcss -i ./dist/input.css -o ./public/asset/css/style.css --watch' > project-watch.sh

# MESSAGE SUCCESS
printf "\n\n::::::::PROJECT CREATED::::::::\n\nRun\n"
printf "$ sh project-server.sh ; # to start server\n"
printf "$ sh project-watch.sh ; # to run Tailwind CSS watch\n\n"
